void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float d = 0.3;

    float lon = atan((0.5 - uv.x) / d) + iTime;
    float lat = atan((0.5 - uv.y) / d) + iTime;

    vec2 equi = vec2(lon * 0.87758, lat - 0.87758);


    // Output to screen
    fragColor = texture(iChannel0, equi);
}
