vec3 red = vec3(1.,0.,0.);
vec3 blue = vec3(0.,0.,1.);
vec3 green = vec3(0,1.,0.);
vec3 yellow = vec3(1.,1.,0.);
vec3 black = vec3(0.,0.,0.);
vec3 gray = vec3(.5,.5,.3);

float quad(vec2 uv,float h, float w,float posX, float posY)
{
    float quad = step(posX-h ,uv.x)-step(posX+h,uv.x);
	quad = quad + step(posY-w,uv.y)-step(0.,uv.y)-step(posY+w,uv.y);
    return quad;
}
float circle(vec2 uv,float distanceAa, float distanceAb,float size)
{

    float val = distance(uv, vec2(distanceAa, distanceAb));

	val = step(val, size);

    return val;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec3 value = mix(cos(iTime*-.9)+blue,yellow,circle(uv,sin(iTime)+.005,cos(iTime)+.003,.05));
    value = mix(value,green,circle(uv,.15,.15,.5));
    value = mix(value,red,circle(uv,.1,.3,.01));
    value = mix(value,red,circle(uv,.6,.3,.01));
    value = mix(value,red,circle(uv,.3,.25,.01));
    value = mix(value,red,circle(uv,.45,.34,.01));
    value = mix(value,gray,quad(uv,.5,.1,.5,.05));


    // Output to screen


    fragColor = vec4(value,uv);
}
