uint wang_hash(uint seed)
{
    seed = (seed ^ 61u) ^ (seed >> 16);
    seed *= 9u;
    seed = seed ^ (seed >> 4);
    seed *= 0x27d4eb2du;
    seed = seed ^ (seed >> 15);
    return seed;
}

uint mur3_hash(uint seed)
{
	seed = seed ^ (seed >> 16);
    seed = seed * 0x85ebca6bu;
    seed = seed ^ (seed >> 13);
    seed = seed * 0xc2b2ae35u;
    seed = seed ^ (seed >> 16);
    return seed;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uint index = uint(int(fragCoord.x + fragCoord.y * iResolution.x));

#if 1
	uint hash = wang_hash(index);
#else
	uint hash = mur3_hash(index);
#endif

    // Output to screen
    fragColor = vec4(float(hash) / pow(2.0, 32.0));
}
