#define PI acos(-1.)
#define TAU PI * 2.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x / iResolution.y;
    float offset = (1. - ratio) * 0.5;
    vec2 st = fragCoord / iResolution.xy;
    st.x *= ratio;
    st.x += offset;

    vec2 pos = st - 0.5;
    float r = length(pos);
    float a = atan(pos.y, pos.x) + iTime;

    float f = sin(a * 3.);

    vec3 col = vec3(smoothstep(0.3, r, f), 0., 0.);

    fragColor = vec4(col, 1.0);
}

#define PI acos(-1.)
#define TAU PI * 2.

vec2 mainSound( in int samp, float time )
{
    float sound = 0.;

    for (float i = 1.; i < 14.; i += 1.) {
    	sound += sin(time * (60. + sin(time * 10.) * 1.) * i * TAU) * 0.1;
    }

    return vec2(sound);
}
