#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
    for(float j=0.0;j<4.3;j+=.1) {
	    for(int i=0;i<3;i++) {
		    vec2 uv,p=fragCoord.xy/r;
		    uv=p;
		    p-=.5;
		    p.x*=r.x/r.y;
		    z+=0.3;
		    l=length(p);
            uv+=p/l*(sin(z)+(j/((sin(t*.1)+.001)*4.)))*abs(sin((l*j)+z*(j+0.6)));
		    c[i]=.02/length(abs(mod(uv,1.)-2.7/j));
        }
	}
	fragColor=vec4(c/l,t);
}
