void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 l = B(U);
    Q.xy = vec2(0);

    vec4 a = vec4(0);
    vec2 C = -6.28318530718*(U-.5-0.5*R)/R;
    for (float i = 0.; i < R.y; i++) {
        a = A(vec2(U.x,i));
        Q.xy += cmul(a.zw,C.y*i);
    }
    vec2 u = U-0.5*R;
    vec2 b = U-0.3*R;
	if (iFrame < 1) 
        Q = exp(-.003*dot(u,u))*vec4(0,0,cos(u.x),sin(u.x))+
        	exp(-.001*dot(b,b))*vec4(0,0,cos(b.y),sin(b.y));

}

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)

vec2 cmul (vec2 a,float b) { return a*mat2(cos(b),-sin(b),sin(b),cos(b)); } 
vec2 cmul (vec2 a,vec2  b) { return mat2(a,-a.y,a.x)*b; } 

vec2 pol (vec2 a) {return vec2(length(a),atan(a.y,a.x));}
vec2 car (vec2 a) {return a.x*vec2(cos(a.y),sin(a.y));}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    vec2 C = -6.28318530718*(U-.5-0.5*R)/R;
    for (float i = 0.; i < R.x; i++)
        Q.xy += cmul(A(vec2(i,U.y)).xy,C.x*i);

  	float a = -600.*sin(.2*iTime)*dot(C,C);
    // Klein-Gordon :
  	//float a = -600.*sin(.2*iTime)*length(C);
    Q.xy *= mat2(cos(a),-sin(a),sin(a),cos(a));
}

