float hash21(vec2 n) {
	n = fract(n * vec2(324.656, 95.23766));
    n += dot(n, n+756.42);
    return fract(n.x + n.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	uv += iTime * .05;
    //uv.y += iTime * .02;

    vec3 bck = vec3(155.0, 188.0, 15.0)*1.0/255.0;
    vec3 frg = vec3(15.0, 56.0, 15.0)*1.0/255.0;

    vec3 col = vec3(0);

    float period = abs(sin(iTime*.2)) + .5;

    uv *= 15.;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float h = hash21(id) * period;

    if (h < .5) gv.x = -gv.x;

    float mask = smoothstep(.25, .256, abs(gv.x)) * sign(gv.y*sign(gv.x));
    mask += smoothstep(.25, .256, abs(gv.y)) * sign(gv.x*sign(gv.y));
    mask = min(max(mask, 0.0), 1.0);

    col = mix(vec3(0.5,0.0,0.0), vec3(0.0,0.5,0.0), mask);
    col = mix(bck, frg, mask);

    //if (gv.x > .48 || gv.y > .48) col = vec3(1,0,0);

    fragColor = vec4(col,1.0);
}
