float tilesY = 8.0;
float tilesX = 8.0;

float tileWidth = 30.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 uv = fragCoord/iResolution.xy;

	vec4 col1 = vec4(1.0, 1.0, 1.0, 1.0);
	vec4 col2 = vec4(0.0, 0.0, 0.0, 1.0);

	int xPos = int(fragCoord.x / tileWidth);
	int yPos = int(fragCoord.y / tileWidth);

	int totalPosition = yPos % 2 + xPos;

	vec4 finalCol = float(totalPosition % 2) * col1 + (1.0-float(totalPosition % 2)) * col2;

    if(float(xPos)*tileWidth > tileWidth*tilesX || float(yPos)*tileWidth > tileWidth*tilesY)
        finalCol = col2;

	fragColor = finalCol;
}
