float character(int n, vec2 p)
{
	p = floor(p*vec2(4.0, -4.0) + 2.5);
    if (clamp(p.x, 0.0, 4.0) == p.x)
	{
        if (clamp(p.y, 0.0, 4.0) == p.y)	
		{
        	int a = int(round(p.x) + 5.0 * round(p.y));
			if (((n >> a) & 1) == 1) return 1.0;
		}	
    }
	return 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pix = fragCoord.xy;
	vec3 col = texture(iChannel0, floor(pix/8.0)*8.0/iResolution.xy).rgb;	

	float gray = 0.3 * col.r + 0.59 * col.g + 0.11 * col.b;


    int n = 0;
    if (gray > 0.1) n = 9616687; // R
    if (gray > 0.3) n = 32012382; // S
    if (gray > 0.5) n = 16303663; // D
    if (gray > 0.7) n = 9616687; // R
	vec2 p = mod(pix/4.0, 2.0) - vec2(1.0);

	//if (iMouse.z > 0.5)	col = gray*vec3(character(n, p));
	//else col = col*character(n, p); // Mouse Click Event

    col = col*character(n, p);

    fragColor = vec4(col,1.0);
}
