#define PI 3.1415

void rz(inout vec2 p, float a) {
    vec2 q = p;
    p.x = q.x * cos(a) - q.y* sin(a);
    p.y = q.x * sin(a) + q.y * cos(a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 0.6;

    vec2 st;
    vec2 uvr = uv;
    vec4 clrOut;
    float d,rdsq,dis,col,sharp,shape,lineLength,a,Sharp,t;
    a = atan(uv.y,uv.x);
    Sharp = .75; 
    t = -iTime * 0.1;

    d = length(uv);

    rz(uvr,t*.75);
    rdsq = pow( pow(uvr.x,8.)+pow(uvr.y,8.),1./(8.) );

    shape = ( abs( sin(rdsq*30.)) - 
             .7 * (1.+0.3*sin(a*8.+240.*PI*sin(t*PI/5.)*sin(d*.25))) * 
             (1.25+ .25*sin( (1.2*sin(t*2.*PI/10.)) *300.*sin(d*.1))) * 
             (1.+0.1*sin(a*16.-4.*PI*t)) );

    st = vec2(d*2., shape);
    lineLength = .25-.45*cos(t*5.)+(1.-abs(-cos(t*20.)))*.2;
    dis = length(st - vec2(clamp(st.x, -lineLength, lineLength), 0.));

    col = smoothstep(Sharp, -Sharp, dis);

    clrOut = col * vec4(0.0,1.0,0.0,1.);
    clrOut = mix(clrOut, vec4(1.,.0,.0,.0)*col, d*3.);

    fragColor = clrOut;
}
