float triWave(float x) {
	return(abs(mod(x-10.,20.)-10.)+1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv*2.-1.;
    float mouse = iMouse.x/iResolution.x*10.+1.;
    vec2 uvR;
    vec2 uvG;
    vec2 uvB;
    if(iMouse.z>0.) {
    	uvR = uv*(1.-length(uv)/mouse);
    	uvG = uv*(1.-length(uv)/mouse+.005);
    	uvB = uv*(1.-length(uv)/mouse+.01);
    } else {
    	uvR = uv*(1.-length(uv)/(triWave(iTime*5.)));
    	uvG = uv*(1.-length(uv)/(triWave(iTime*5.+.1)));
    	uvB = uv*(1.-length(uv)/(triWave(iTime*5.+.2)));
    }
    uvR = uvR/2.+.5;
    uvG = uvG/2.+.5;
    uvB = uvB/2.+.5;
    float R = texture(iChannel0, uvR).r;
    float G = texture(iChannel0, uvG).g;
    float B = texture(iChannel0, uvB).b;
    fragColor = vec4(R,G,B,1.);
}
