mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,-s,s,c);}
float sdBox( vec3 p, vec3 b )
{
 vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float dist(vec3 p){
    p.xz*=rot(p.y*4.+iTime);
    p.yz*=rot(iTime);
    p.yz*=rot(3.14/2.);
	return length(p)-0.01;
   // return sdBox( vec3 p, vec3 b );
}

vec3 norm(vec3 p){
    vec2 e = vec2(0.1,0.);
	return normalize(vec3(
    	dist(p+e.xyy)-dist(p-e.xyy),
    	dist(p+e.yxx)-dist(p-e.yxx),
        dist(p+e.yxx)-dist(p-e.yxx)
    ));
}

void mainImage( out vec4 fragColor, in vec2 FC )
{

    vec2 uv=(FC*2.0-iResolution.xy)/iResolution.y;
    float e,d=10.;
    vec3 p,rd=vec3(uv,0.2);
    int i;
    for(i=0;i<3;i++){
        p=rd*d-vec3(rot(iTime));
        d+=e=dist(p);
        if(e<.01)break;
    }
  p.xy*=rot(iTime);
    vec3 col=norm(p)*.5+.5;
    fragColor = vec4(vec3(col),1.0);
}
