void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 center = iResolution.xy * .5;
    float extent = min(iResolution.x, iResolution.y) * .5;

    vec2 lCenter = center;
    float lExtent = extent * .9375;

    fragColor = vec4(
        			vec3(length(fragCoord - lCenter) < lExtent ? 1. : 0.),
        			1.);

    vec2 delta01 = texture(iChannel0, vec2(iTime / 1024.)).xy;
    vec2 delta = (delta01 * 2. - 1.) * lExtent * .9375;

    vec2 sCenter = center + delta;
    float sExtent = 4.;

    fragColor = length(fragCoord - sCenter) < sExtent
        		? vec4(vec3(0.), 1.)
        		: fragColor;
}
