float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    uv-=.5;
    float r =iResolution.x/iResolution.y;
    uv.x*=r;



    vec2 st = vec2(0.);

    for(int i = 0; i< 6; i++){

    st.x = (abs(fract(iTime*.2
                     +abs(1.-random(vec2(float(i),0.)))
                     )-.5)-.25)*2.;

    st.y = (abs(sin(iTime
                   +abs(1.-random(vec2(float(i),float(i))))
                   ))*abs(1.-random(vec2(0.,float(i))))
            -.5);
    float ff = smoothstep(0.05,0.053,length(uv-st));

    col*=ff;

    }
    col *= step(abs(uv.x*r),1.);


    // Output to screen
    fragColor = vec4(col,1.0);
}
