#define PI 3.14159265359
mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime *-0.3;
    vec2 u = uv;
    float ratio = iResolution.x/iResolution.y;
    u.x = fract(u.x * 2.);
    float offset = floor(uv.x + 0.5);
    t += offset;
    u -= vec2(0.5);
    u = rotate2d( t * PI) * u; 
    //float d = length(u);
    //float c = step(d, 0.4);
    //float c2 = step(d, 0.2);
    //float g = fract(u.x)*fract(u.y);
    float k = fract(atan(u.y,u.x)*0.2);
    fragColor = vec4(k);
}
