vec4 rgbToCmyk(in vec3 rgb){
    float r=rgb.r;
    float g=rgb.g;
    float b=rgb.b;
    float k=min(min(1.0-r,1.0-g),1.0-b);
    float c=(1.0-r-k)/(1.0-k);
    float m=(1.0-g-k)/(1.0-k);
    float y=(1.0-b-k)/(1.0-k);
    return vec4(c,m,y,k);

}



vec2 offset(in float rad,in float radius){
    return vec2(cos(rad),sin(rad))*radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float radius=min(0.0,sin(iTime*3.0)*0.125);

    vec4 fgK=rgbToCmyk(texture(iChannel0,uv).rgb);
    vec4 fgC=rgbToCmyk(texture(iChannel0,uv+offset(iTime+radians(0.0),radius)).rgb);
    vec4 fgM=rgbToCmyk(texture(iChannel0,uv+offset(iTime+radians(120.0),radius)).rgb);
    vec4 fgY=rgbToCmyk(texture(iChannel0,uv+offset(iTime+radians(240.0),radius)).rgb);

    // Output to screen
    vec3 col=vec3(1);
    col-=vec3(fgK.a);
    col-=vec3(fgC.r,0.0,0.0);
    col-=vec3(0.0,fgM.g,0.0);
    col-=vec3(0.0,0.0,fgY.b);
    fragColor = vec4(col,1.0);
}
