void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 xy = fragCoord/iResolution.xy;
    xy*=2.;
    float m1=step(xy.x,1.);
    float m2=step(1.,xy.x);
    vec4 texColor=m1*texture(iChannel0,vec2(xy.x,xy.y/2.))+
                  m2*texture(iChannel0,vec2(2.-xy.x,xy.y/2.));

    fragColor=texColor;



}
