const mat3 Yab_TO_RGB = mat3(3., 3., 3., 4., -2., -2., 0., sqrt(3.), -sqrt(3.))/3.;
const mat3 RGB_TO_Yab = mat3(4., 6., 0., 4., -3., 6.*sqrt(3.), 4., -3., -6.*sqrt(3.))/12.;

vec3 Yab_to_RGB(vec3 Yab) { return Yab_TO_RGB*Yab; }
vec3 RGB_to_Yab(vec3 rgb) { return RGB_TO_Yab*rgb; }

vec3 YCH_to_Yab(vec3 YCH) { return vec3(YCH.x, YCH.y*vec2(cos(YCH.z), sin(YCH.z))); }
vec3 Yab_to_YCH(vec3 Yab) { return vec3(Yab.x, length(Yab.yz), atan(Yab.z, Yab.y)); }

vec3 YCH_to_RGB(vec3 YCH) { return Yab_to_RGB(YCH_to_Yab(YCH)); }
vec3 RGB_to_YCH(vec3 rgb) { return Yab_to_YCH(RGB_to_Yab(rgb)); }

void mainImage(out vec4 rgba, vec2 xy) {
	const float tau = radians(360.0);
    vec2 uv = xy/iResolution.xy;
    vec3 hsl = vec3(uv.x, 0.5 + 0.5*cos(iTime), uv.y);
    vec3 ych = vec3(hsl.z, hsl.y*hsl.z*(1.0-hsl.z), tau*hsl.x);
   	vec3 rgb = YCH_to_RGB(ych);
    rgba = vec4(rgb, 1.0);
}
