renderpass.code:
#define T(U)         texelFetch( iChannel0, ivec2(U) % 1024, 0 )
#define val(U, X,Y)  step(  s(U,Y) , T( U+vec2(X,Y) ).x )

// threshold.  left: constant.  right: slight density gradient
#define s(U,Y)         ( u.x < R.x/2.                           \
                           ? .41                                \
                           : .3+.4*(floor(U.y)-iTime+Y)*z/R.y   \
                       )

void mainImage( out vec4 O, vec2 u )
{
    float z = 8.;
    vec2 U = u/z + iTime,
         R = iResolution.xy,
         I = round(U), F = U-I, S = step(0.,F); 
    F = abs(F); I--;

    if (int(u)==int(R/2.)) { O = vec4(1,0,0,1); return; } // red separator

    O = vec4(val(U, 0.,0.) );                             // thresholded blue-noise
    if (fract(iTime/4.-.4) > .5 ) return;
 // O = vec4( 10.*( texture(iChannel0, U/R).x - s ) ) );

    vec4 V = vec4( val(I, 1.-S.x,S.y), val(I, S.x,S.y), 
                   val(I, S.x,1.-S.y), val(I, 1.-S.x,1.-S.y));

    if (      V.x == V.z && V.x != V.y // && F.x+F.y<.5   // round corners + white passages 
         && ( V.y>0.     || V.y != V.w ) )
     // O = 1.-O;
        O = mix( O, 1.-O, smoothstep(-1./16.,1./16.,.5-(F.x+F.y)) );
}
