vec3 back = vec3(1.0, 1.0, 1.0);
vec3 new_blood = vec3(1.0, 0.001, 0);
vec3 old_blood = vec3(0.5,0.001,0);
float bloody_time = 0.1;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 params = texture(iChannel0, uv);
	float time = clamp(bloody_time * iTime, 0.0, 1.0);
    float alpha = params.b;
    float time_func = (alpha - 1.0 + time) / (time);

    vec3 blood = mix(old_blood, new_blood, params.r);

    vec3 col = mix(back, blood, alpha * time_func);
    // Output to screen
    fragColor = vec4(col,1.0);
}
