/**
*
* A simple helper demo for generating a Halton(2, 3) sequence for some TAA stuff.
*
* The sequence adds a new offset to the index every 60 frames (or 1 second, this can be changed). So I can 
* scrub the time (shadertoy plugin <3) and see where the discrepancy of the sequence is the lowest and 
* calculate the offset value at that timestep. 
*
* To calculate offset, offs = floor((t * 1000 / 16.67) / interval)
*
* The above calculation assumes a stable framerate of 60 fps. 
*
*/

//#define GRID

const int interval = 60;
const int sampleCount = 16;

// https://en.wikipedia.org/wiki/Halton_sequence#Implementation_in_pseudocode
float halton(int base, int index)
{
	float result = 0.;
	float f = 1.;
	while (index > 0)
	{
		f = f / float(base);
		result += f * float(index % base);
		index = index / base; 
        //index = int(floor(float(index) / float(base)));
	}
	return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.);

    int offset = iFrame / interval; // increment offset every x no. of frames

    for (int i = 0; i < sampleCount; ++i)
    {
        int idx = offset + i;
        vec2 hh = vec2(halton(2, idx), halton(3, idx)) - .5;
        hh.x *= iResolution.x / iResolution.y;
    	col += smoothstep(.02, .01, length(uv - hh * 1.8));
    }

#ifdef GRID
    vec2 st = fragCoord / iResolution.xy;
    st *= 4.;
    if (fract(st).x < .01 || fract(st).y > .99)
        col.r += 1.;
#endif

    fragColor = vec4(col, 1.);
}
