const float invertedSquareSize = 30.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float intensity = ( iMouse.x / iResolution.x );

   vec2 Texcoord = vec2( fragCoord ) / vec2( iResolution );

   vec2 sampleDivisor = vec2(intensity / invertedSquareSize, intensity / invertedSquareSize );
   vec2 samplePos =  Texcoord - mod(Texcoord, sampleDivisor);

   vec4 inputColor = texture(iChannel0, samplePos );
   fragColor = vec4(inputColor.rgb, inputColor.a); 
}
