#define PI 3.1415

float rings(vec2 uv, vec2 center, float w, float period)
{
    float d = length(uv-center);
    return smoothstep(0.,w,0.5 * (1.+sin(2. * PI * d/period)) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = .25;

    float h = 1./10. * iTime;
    float m = h * 2.;
    float s = m * 2.;

    vec2 uv = fragCoord/iResolution.xx;
    vec2 center = .5 * iResolution.xy / iResolution.xx ;

    vec2 h_off = radius * vec2(cos(h), sin(h));
    vec2 m_off = radius * vec2(cos(m), sin(m));
    vec2 s_off = radius * vec2(cos(s), sin(s));

    float dst = rings(uv, center + h_off, .5, .01);
    dst = min(dst, rings(uv, center + m_off, .5, .01));
    dst = min(dst, rings(uv, center + s_off, .5, .01));

    dst = max(smoothstep(radius,radius+.01, length(uv - center)), dst);

    vec3 col = vec3(1.,1.,1.) * dst;

    fragColor = vec4(col,1.0);
}
