0.003765, 0.015019, 0.023792, 0.015019, 0.003765
);
vec2 aryCoords[25] = vec2[](
    vec2(-2.0,-2.0), vec2(-1.0,-2.0), vec2( 0.0,-2.0), vec2( 1.0,-2.0), vec2( 2.0,-2.0),
    vec2(-2.0,-1.0), vec2(-1.0,-1.0), vec2( 0.0,-1.0), vec2( 1.0,-1.0), vec2( 2.0,-1.0),
    vec2(-2.0, 0.0), vec2(-1.0, 0.0), vec2( 0.0, 0.0), vec2( 1.0, 0.0), vec2( 2.0, 0.0),
    vec2(-2.0, 1.0), vec2(-1.0, 1.0), vec2( 0.0, 1.0), vec2( 1.0, 1.0), vec2( 2.0, 1.0),
    vec2(-2.0, 2.0), vec2(-1.0, 2.0), vec2( 0.0, 2.0), vec2( 1.0, 2.0), vec2( 2.0, 2.0)
);
vec4 cal( vec2 fragCoord, vec2 dir )
{
    vec4 color = vec4( 0.0 );
    float a = 0.95;
    for( int i = 0; i < 20; ++i )
    {
        float f = pow( 1.25, float( i ) );
        vec2 coord = fragCoord + dir * f;
        vec4 src = texture( iChannel1, coord/iResolution.xy );
        vec4 mask = texture( iChannel0, coord/vec2( 512.0 ) );
        color += src * mask * pow( a, float( i ) );
    }
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec4 color = vec4( 0.0 );
    color += cal( fragCoord, vec2( -1,  0 ) );
    color += cal( fragCoord, vec2(  1,  0 ) );
    color += cal( fragCoord, vec2(  0,  1 ) );
    color += cal( fragCoord, vec2(  0, -1 ) );
    for( int i = 0; i < 25; ++i )
    {
    	// Normalized pixel coordinates (from 0 to 1)
    	vec2 uv = (fragCoord+aryCoords[i])/iResolution.xy;
        float l = length( aryCoords[i] );
        //float weight = 1.0 - pow( l / 7.0, 2.0 );
        float weight = aryWeights[i];
    	color += texture( iChannel1, uv ) * weight;
    }
    color = mix( vec4( 0.0, 0.0, 0.0, 1.0 ), color, color.a );
    fragColor = color;
}
