// (note that hue is only approximative here ).

// inflates the mid-intensity area:
#define smooth(l) ( .5 + sign(l-.5)*pow(abs(l-.5), 1.2) )
//#define smooth(l) sqrt(l)
//#define smooth(l) (l)

#define S(v) smoothstep( 5./R.y, 0., v )

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
         U = ( U+U - R ) / R.y;
    float l = length(U), a = atan(U.y,U.x);

    O = smooth(l) * ( .6 + .6 * cos( a  + vec4(0,23,21,0)  ) ); // hue: https://www.shadertoy.com/view/ll2cDc
    // draw region
    if ( .75*O.r > max(O.g,O.b) ) O = max(O, S( abs(O.r-.92) ) );
    if ( U.x > 0. && O.r < .92  ) O = max(O, S( min(abs(.75*O.r-O.g),abs(.75*O.r-O.b)) ));
  //O += vec4( .75*O.r > max(O.g,O.b) && abs(O.r-.92) < 3./R.y );
  //O += vec4( U.x > 0. && O.r<.92 && min(abs(.75*O.r-O.g),abs(.75*O.r-O.b)) < 3./R.y );
}
