void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    vec2 uv;
    vec2 p;
    vec2 r = iResolution.xy;

    for (int i = 0; i < 3; i++) {
        uv = fragCoord/r;
        p = uv;

        uv -= 0.5;

        p.y -= 5. * sin(iTime * 0.15);
        p.x -= 5. * cos(iTime * 0.15);

        uv.y += sin(iTime) * 0.15;
        uv.x += cos(iTime) * 0.15;

        col[i] += p.x / (uv.x * 0.01);
        col[i] += p.y / (uv.y * 0.01);

        col[i] += cos(p.x / uv.x) / 0.001;
        col[i] += sin(p.y / uv.y) / 0.001;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
