#define NUM_LAYER 10.
mat2 Rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}
float Star(vec2 uv, float flare){
    float d = length(uv);
    float m =.005/d;

    float rays =max(0., 1.-abs(uv.x*uv.y*300.));
    m+=rays*flare;

    uv*=Rot(3.1415/4.);
    rays =max(0., 1.-abs(uv.x*uv.y*300.));
    m+=rays*.3*flare;
    m*=smoothstep(.9,.2,d);
    return m;
}
float Hash21(vec2 p){
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x + p.y);
}
vec3 StarLayer(vec2 uv, float t){
    vec3 col=vec3(0);
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv)+.5;
    for(int y=-1;y<=1;y++){
        for(int x=-1;x<=1;x++){
            vec2 offs = vec2(x,y);        

            float n = Hash21(id+offs);
            float size = fract(n*345.2)*5.*(t*2.-n);
            float star = Star(gv-offs+(vec2(n,fract(n*10.))-.5),.6);
            vec3 color = vec3(.2,.9,1.);
            color*=vec3(.0,1.,1.);
            col+=star*size*color;
        }
    }
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	float t = iTime*.5;
    uv*=2.;

    vec3 col = vec3(0);

    for(float i=0.;i<1.;i+=1./NUM_LAYER){
        float depth = fract(i);
        //float fade = depth*smoothstep(1.,.9,depth);
        float scale = mix(20.,.5,depth);

    	col+=StarLayer(uv*scale+i*64.,sin(t)*.5+.5);
    }

    fragColor = vec4(col,1.0);
}
