void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(1.0);
    col += sin(uv.x * 3.0 + cos(iTime / 100.0) * 300.0) + cos(uv.y * 12.0 + sin(iTime / 40.0) * 10.0);
    col += sin(uv.x * 7.0 + sin(iTime / 100.0) * 20.0) + cos(uv.y * 20.0 + cos(iTime / 50.0) * 40.0);
    col.x += sin(uv.y * 3.0 + sin(iTime / 40.0) * 16.0);
    col.y += cos(uv.y * 23.0 + sin(iTime / 40.0) * 15.0);
    col.z += sin(uv.y * 18.0 + sin(iTime / 40.0) * 14.0);
    col = sin(col * 0.9) * 0.1 + sin(iTime * col) * 0.9;

    fragColor = vec4(col, 1.0);
}
