//Rings as masks blackening the terrain rings instead of coloring them in

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Normalized pixel coordinates (from 0 to 1)
   vec2  R = iResolution.xy;
   float h = R.y;
   float d =   h / ( h + length(fragCoord - iMouse.xy) ) // Pole at mouse
        	 - h / ( h + length(fragCoord - .5*R     ) );// Zero at center screen
   //d *= 2.; //increase the slope of the terrain, looks sharper
   vec3 c2 = vec3(-d, d, 0.); // gradient color assignment; negative values are black

   // using d as terrain 'height' or float indicating brightness
   float bright = abs(d) + 1. - h * abs( d - round(d*20.)/20. );

   fragColor =  -bright * vec4(max(0.,-d), max(0.,d), 0, 1);

}
