void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    /*vec2 uv = fragCoord/iResolution.xy;
	float aspect = iResolution.x/iResolution.y;
    uv.x *= aspect;*/
    vec2 uv = ( fragCoord - .5*iResolution.xy) /iResolution.y;
    float sen = 0.25;
    float fft  = (
        texture(iChannel0, vec2(.0,sen*1.2)).x +
        texture(iChannel0, vec2(.3,sen)).x +
        texture(iChannel0, vec2(.6,sen)).x +
        texture(iChannel0, vec2(.9,sen)).x
    );
    float high = (
        texture(iChannel0, vec2(.7,sen)).x +
        texture(iChannel0, vec2(.9,sen*2.)).x
    );
    float low = (
        texture(iChannel0, vec2(.0,sen*2.)).x+
        texture(iChannel0, vec2(.05,sen)).x+
        texture(iChannel0, vec2(.1,sen)).x
    );


    float b = 2.0-length(vec2(uv.x,uv.y));
    float g = length(vec2(uv.x,uv.y))-0.5
        >
        (0.09)*low*(sin(3.*(atan(uv.x,uv.y)+sin(iTime)))+1.)
        -.2*(getAnim(iTime)*low)
        ? farbe(iTime)*b : (1.-farbe(iTime))*b;

    // Time varying pixel color
    vec3 col = vec3(g*abs(sin(iTime*0.1)),0.,g);

    // Output to screen
    fragColor = vec4(col,1.0);
}

bool swi = false;
float speed = 1.;

float getAnim(float t){
    return tan(speed*t)*1.;
}

float farbe(float t){
    float anim = cos(speed*t);
    if(abs(anim)==1.)
       swi = !swi;

    if(swi)
        return (anim<0.?1.:0.);
    else
        return (anim>=0.?1.:0.);
}

