void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 distort1 = vec2(cos(iTime + uv.y * 5.) * 0.05 - cos(iTime) * 0.1, -iTime*0.3);
    vec2 distort2 = vec2(sin(iTime + uv.y * 2.) * 0.025 - cos(iTime) * 0.1, -iTime*0.5);
	float spark1 = 1.-texture(iChannel0, uv + distort1).r;
    float spark2 = 1.-texture(iChannel0, uv + distort2).r;
    float brightness = 8.;
    float spark = (pow(spark1, brightness) + pow(spark2, brightness)) * exp(-dot(uv-0.5, uv-0.5) * 2.);
    vec3 col = vec3(1.0, 0.7, 0.3) * spark * vec3(1.0, spark, spark);
    fragColor = vec4(col,1.0);
}
