void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 uvBricks = uv + iTime / 16.0;
    uvBricks *= vec2(2.0, 2.0);

    vec2 uvNoise = uv + iTime / -32.0;
    uvNoise *= vec2(0.25, 0.25);

    vec4 noiseOffset = texture(iChannel1, uvNoise);

    uvBricks -= noiseOffset.r * 0.25;

    vec4 bricks = texture(iChannel0, uvBricks);

    fragColor = bricks;
}
