void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t  = mod(iTime/5.0, 1.0);
    float ps = smoothstep(0.0, 1.0, abs(t*2.0 - 1.0));
    float p  = ps * 0.5 + 1.0;

    vec2 wpos = fragCoord/iResolution.xy*2.0 - vec2(1, 1);

    wpos.x += sin(wpos.y*10.0 + iTime)*((1.0-ps)*0.05+0.05);
    wpos    = vec2(pow(abs(wpos.x), p)*sign(wpos.x), pow(abs(wpos.y), p)*sign(wpos.y));

    vec2 uv = (wpos + vec2(1, 1)) / 2.0;
    fragColor = texture(iChannel0, uv);

    t = mod(iTime/7.0 + 0.1, 1.0);
    float c = smoothstep(0.0, 1.0, abs(t*2.0 - 1.0)) * pow(abs(wpos.x*wpos.y)/2.0 + 0.5, 0.5);

    fragColor.r *= 1.0-c;
    fragColor.g *= c;
}
