// Robinson R1 tiling
//https://github.com/isohedral/frag/blob/master/Examples/robinson.frag


float getSquares( vec2 p, float th, float fw )
{
	vec2 m = mod( p, 2.0 );
	m = min( m, 2.0 - m );
	float s = min(m.x,m.y);
	float d = abs( 0.5 - s );
	return smoothstep( th - fw, th + fw, d );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    vec4 col; 
    vec2 wpos=fragCoord*0.035;

	float c = 1.0;
	float th = 0.125;
	float fw = 0.002;
	vec2 p = wpos;

	for( int idx = 0; idx < 20; ++idx ) {
		c *= getSquares( p, th, fw );

		p *= 0.5;
		th *= 0.5;
		fw *= 0.5;
	}

	col = vec4( c, c, c, 1.0 );
      fragColor = col;
}
