mat2 Rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Triangle( in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    uv*=17.;
    vec3 col = vec3(0);
    uv.y*=.58;
    uv.y=abs(uv.y);
    uv.x=abs(uv.x);

    uv += mod(uv.x, uv.y);
    uv.x += 1.-uv.y;
    uv.y -= .3;

    float t = iTime * .3;

    float s1 = 1.47;
    float s2 = 0.16;

    uv*=Rot(-t*.4);

    uv*=Rot(t);
    col.r += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(-t);
    uv*=Rot(t*.4);
    col.b += smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y)))*1.;
    uv*=Rot(-t*.4);
    uv*=Rot(-t*1.24);
    col.g += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(t*1.24);

    uv*=Rot(-t);
    col.rg += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(t);
    uv*=Rot(-t*.71);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y+(1.*sin(iTime)*.5+.5))))*1.;
    uv*=Rot(t*.3);
    uv*=Rot(t*1.24);
    col.b += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.1);

    uv*=Rot(t*1.78);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.78);

    float t1 = sin(t);
    col -= smoothstep(0.81+t1*.1, 0.8+t1*.1, length(uv));

    col.rg += smoothstep(0.41, 0.4, length(uv));

    uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    col *= smoothstep(.47, -.2, length(uv.y));
    uv*= 12.;
    uv.y*=.58;
    uv.y=abs(uv.y);
    uv.x=abs(uv.x);

    uv += dot(uv.x*uv.x*uv.x, uv.y);

    uv *= Rot(2.);

    t = iTime * .7;
    uv*=Rot(t*.17);

    s1 = 0.17;
    s2 = 0.16;

    uv*=Rot(t);
    col.b += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(-t);
    uv*=Rot(t*.4);
    col.g += smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y)))*1.;
    uv*=Rot(-t*.4);
    uv*=Rot(-t*1.24);
    col.g += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(t*1.24);

    uv*=Rot(-t);
    col.r += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(t);
    uv*=Rot(-t*.71);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y+(1.*sin(iTime)*.5+.5))))*1.;
    uv*=Rot(t*.3);
    uv*=Rot(t*1.24);
    col.b += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.1);

    uv*=Rot(t*1.78);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.78);

    t1 = sin(t);
    col -= smoothstep(0.81+t1*.1, 0.8+t1*.1, length(uv));

    col -= smoothstep(0.41, 0.4, length(uv));

    uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    col *= smoothstep(.47, -.2, length(uv.y));

    col += smoothstep(.1, .0, length(uv))*2.;
    col *= smoothstep(.3, .0, length(uv));

    col *= vec3(1., 0.5, 1.);

    fragColor = vec4(col,1.0);
}
