#define BITS 10

float xor(float a, float b) {
	float mask = pow(2.0, float(BITS));
	float c = 0.0;

	if (a < 0.0) a = mask - a;
	if (b < 0.0) b = mask - b;

	for (int i = 0; i < BITS; i++) {
		mask *= 0.5;
		c *= 2.0;
		c += mod(floor(a / mask) + floor(b / mask), 2.0);
        c+=2.0*abs(sin(c*0.5));
	}

	return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float x = floor(fragCoord.x);
	float t = floor(fragCoord.y + iTime * 20.0);
	float a = xor(x - t, x + t);
	float c = (mod(abs(t + a*a*a), 997.0) < 97.0) ? 1.0 : 0.0;
	float c1 = (mod(abs(t + a*a*a), 997.0) < 37.0) ? 1.0 : 0.0;
	float c2 = (mod(abs(t + a*a*a), 997.0) < 161.0) ? 1.0 : 0.0;
	fragColor = vec4(c2, c, c1, 1);
}
