void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 buffer0 = texture(iChannel0,fragCoord/iResolution.xy).xy;

    fragColor = texture(iChannel1,buffer0);

    //fragColor = vec4(buffer0,0.,0.);

}

#define brushSize 0.1

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    vec4 buffer = texture(iChannel0, vec2(0));                // indeed reads .5/R, i.e., prev mouse

    if( fragCoord  == vec2(.5) ) fragColor = iMouse;                               // (.5,.5) store mouse
    else if(iFrame<1 ) fragColor = (fragCoord/iResolution.xy).xyxy;         // init: canonical UVs
    else {
        float circle = exp( -length(iMouse.xy-fragCoord )*brushSize );     // mask ( decrease around mouse )
        if ( buffer.w*iMouse.w > 0. )                        // if mouse click
            fragCoord  -= circle * (iMouse-buffer).xy;                     // decrease U by mouse move ( * mask )
        fragColor = texture(iChannel0, fragCoord  / iResolution.xy );         // update UV mapping
    }
}
