#define rot(a) mat2( cos(a), -sin(a), sin(a), cos(a) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u/R,
         M = iMouse.xy/R;
    if (length(M*R)<10.) M = vec2(0.05,.93);

    float v = .5; // try 0 or 1
    if (U.y>.25 && (U.x<.5 || length(U-vec2(.5,.625))< .375 ) )  {
        u /= exp2((5.*M.x));   // wavelength
        u *= rot(1.57*M.y);
        v = .5+.5*sin(3.14*u.x);
    }
    O = vec4( pow(v, 1./2.2)); // to SRGB
}
