void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float rng=5.+30.*(1.+cos(iTime/3.));//sets the size of the display

    //rescale coordinates
    float L=rng*(uv.x-0.5);
    float z=rng*(uv.y-0.5);
    z=360./640.*z;//fix aspect ratio
    float r=sqrt(L*L+z*z);


    //area density
   //change this to change the geometry: sinh for hyperbolic, r for Euclidean
	float aDens=pow(sin(r),2.);//Spherical
   // float aDens=pow(r,2.);//Euclidean
    //float aDens=pow(sinh(r),2.);//Hyperbolic
    float intensity=abs(1./aDens);

    float cappedIntensity=min(intensity,1.);


    // Time varying pixel color
    vec3 blue=1.5*vec3(0.1,0.2,0.35);
    vec3 orange=vec3(1.,177./255.,64./255.);
    vec3 yellow=vec3(0.75,0.75,0.35);
    vec3 mix=(1.-cappedIntensity)*blue+cappedIntensity*orange;

    vec3 col = (0.75+intensity/50.)*mix;
        //intensity*vec3(1.,1.,0.5);


    // Output to screen
    fragColor = vec4(col,1.0);
}
