float circle(vec2 uv,vec2 pos, float size)
    {
        float c = length(uv-pos);
    	c=step(size,c);
        return c;
    }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.yy;
	float xyRatio = (iResolution.x)/(iResolution.y);
    float xMid = (xyRatio*(iResolution.x/2.0))/iResolution.x;
    float cir = circle(uv,vec2(xMid,0.5),0.2);
    float cir2 = circle(uv,vec2(0.2,0.3),0.1);
    // Output to screen
    fragColor = vec4(vec3(cir*cir2),1.0);
}
