void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //0 = no effect, 1 = full strength
   	float strength = abs(sin(iTime));

    //get textures
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 base = texture(iChannel0, uv);
    vec4 tex = texture(iChannel1,uv);

	fragColor = base+ (vec4(.4)-tex) * strength;

   	//determine gray
    float gray = dot(fragColor.rgb, vec3(0.299, 0.587, 0.114));

    //add some sepia
    vec3 sepia = vec3(1.0, 0.8, 0.6);
    vec3 sepiaColour = mix(vec3(gray),sepia,0.2);

    //apply gray
    fragColor.rgb = mix(fragColor.rgb, vec3(sepiaColour), strength*0.95);

}
