#define RANGE 0.2
#define SAMPLES 1000.0
#define SCALE 0.1
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 xy = fragCoord/iResolution.x - vec2(0.5,0.25);;
  vec2 dir,sm,dsm;
  vec2 p1,p2;
  ivec2 tp;
  float s;
  float i,di;
  s=0.0;

  p1=xy + vec2(0.3*sin(iTime*0.4),  0.20*sin(iTime*0.563));
  p2=xy + vec2(0.3*sin(iTime*0.221),0.20*sin(iTime*0.163));
  di=2.0*RANGE/SAMPLES;
  dir=SCALE*(p1/dot(p1,p1)-p2/dot(p2,p2));
  sm=xy*4.0-dir*RANGE;
  dsm=dir*di;

  for (i=-RANGE;i<=RANGE; i+=di) {
      s+=texture(iChannel0,sm).r; sm+=dsm;
  }
  fragColor = vec4(s/SAMPLES);
}
