float circle(vec2 p,vec2 o,float r){
	return length(p - o) - r;
}
mat2 genRot(float val){
	return mat2(cos(val),-sin(val),sin(val),cos(val));
}
float C(vec2 p,vec2 o,float inR,float outR,float a ,float rot){
    p -= o;
    p *= genRot(rot);
    float tmp1 = p.x + a;
    float tmp2 = max(length(p) - outR,-length(p) + inR);
	return max(tmp1,tmp2);
}
float hexa(vec2 p,vec2 o,float r){
    p -= o;
    vec3 k = vec3(-0.866025404,0.5,0.577350269);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
    return length(p)*sign(p.y);
}
float dist_1(vec2 p){
	float c0 = circle(p,vec2(0.),0.3);
    float c1 = C(p,vec2(0.),0.5,0.7,-0.3,iTime);
    float c2 = C(p,vec2(0.),0.9,1.1,-0.5,-iTime);
    float c3 = C(p,vec2(0.),1.4,1.7,-0.,iTime);

    return min(min(c0,c1),min(c2,c3));
}
float dist_2(vec2 p){
    float result = 100000.0;
    for(float xi = -2.0; xi < 2.0; xi += 0.1){
        for(float yi = -2.0; yi < 2.0; yi += 0.1){
            vec2 tmp = vec2(xi * 2.0 +yi + fract(iTime / 6.0),yi / 1.73);
        	float r = 0.05 - max(dist_1(tmp),0.00);
            result = min(result,hexa(p,tmp,r));
        }
    }
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 col1 = mix(vec3(8,68,102)/255.,vec3(0,168,152)/255.,length(uv));
    vec3 col2 = mix(vec3(182,216,135)/255.,vec3(252,193,121)/255.,length(uv));
	vec3 col = dist_2(uv) > 0. ? col2 : col1;
    fragColor = vec4(col,1.0);

}
