float noise(float t){
	return 0.5 + 0.5 * (sin(t * 4.) + cos(t) + sin(t / 4.)) / 3.;
}
vec2 path(float t){
	float x = cos(t*10.) * (1. + noise(t) * 0.5)/2.;
    float y = sin(t*10.) * (1. + noise(t) * 0.5)/2.;
    return vec2(x,y);
}
float dist(vec2 p,float t){
    p += 0.75;
    p = fract(p / 1.5) * 1.5;
    p -= 0.75;
	float result = 1000000.;
    for(float i = 0.0;i < 2.0; i += 0.0015){
    	float c = length(p - path(t - i)) - 0.0075 * (2.0 - i) / 2.;
        result = min(result,c);
    }
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    uv.x += iTime * 0.2 + abs(uv.y);
	vec3 col = mix(vec3(0.),vec3(1.0),dist(uv,iTime) * 256.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
