// thanks to inigo! and patricio!


float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized screen coords
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // signed distance function in the shape of a circle
	float d = sdCircle(p,0.5);

    // outside of the circle is 0.5 grey, inside is a gradient from top to bottom
	vec3 col = mix(   vec3(-p.y+.5)*1.3, vec3(0.7),abs(smoothstep(0.001,0.005,(d-.01))) );

    // black circle
	col = mix ( vec3(0), col, smoothstep(0.001,.0024,abs(d*0.1)));

	fragColor = vec4(col,1.0);
}
