float map(float i, float s, float t, float x, float y) {
	return i * (y - s) / (t - s) + x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = iMouse.xy/iResolution.xy;
    if (uv == vec2(0.,0.)) {
    	uv = vec2(0.6,0.5);
    }

    float i = fragCoord.x * uv.x ;
    float j = fragCoord.y * uv.y + iTime/100.;
    float t = iTime / 1000.;

    float a = 100.;
    float m = 10.*sin(t*i*j);
    float f = 1.;

    float l = abs(200.*j*cos(i*10.));
    float u=map(m,0.,f,0.,128.)+map(l,0.,300.,0.,127.);
    float col = u / 255.;

    // Output to screen
    fragColor = vec4(col,col, col,1.0);
}
