const float PI = 3.14159265358979323846264338327950288;

float mag(vec2 origin,vec2 coord) {
 	return sqrt(pow(coord.x-origin.x,2.0)+pow(coord.y-origin.y,2.0));
}

float intensity (vec2 origin, vec2 coord) {
    float amplitude = 1.0;
    float frequency = 0.1;
 	return amplitude * sin(2.0*PI *frequency * mag(origin,coord) - iTime);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy/2.0;
    float spacing = (iMouse.y <= 1.0) ? 5.0 : iMouse.y/8.0;

	vec2 point1 = vec2(center.x,center.y-spacing*1.5);
	vec2 point2 = vec2(center.x,center.y-spacing*0.5);
	vec2 point3 = vec2(center.x,center.y+spacing*0.5);
	vec2 point4 = vec2(center.x,center.y+spacing*1.5);

    float value = (intensity(point1,fragCoord)
        		+ intensity(point2,fragCoord)
        		+ intensity(point3,fragCoord)
        		+ intensity(point4,fragCoord))/4.0;

    vec3 col = vec3(value);

    // Output to screen
    fragColor = vec4(col,1.0);
}
