void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    float aspect = iResolution.y / iResolution.x;
    vec3 v=vec3(0.,0.,1.)+vec3(uv.x,0.,0.)+vec3(0.,uv.y*aspect,0.);
    vec3 o=vec3(0.,0.,-2.);
    float r=0.5;

    float a=dot(v,v);
    float b=2.*dot(v,o);
    float c=dot(o,o)-r*r;
    float d=b*b-4.*a*c;

    fragColor = vec4(0.);
    if(d>=0.)
    {
        float t=(-b-sqrt(d))/(2.*a);
        vec3 n=normalize(o+v*t);

        float rot=iTime*0.5;
        vec3 l=normalize(vec3(cos(rot),0.707,-sin(rot)));

        fragColor=vec4(1.)*dot(l,n);
    }  
}
