void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st =(fragCoord.xy/iResolution.xy);
    float mouse_x = iMouse.x/iResolution.x;
    float mouse_y = iMouse.y/iResolution.y;
    float pigs = st.y * st.x;
    float cycle = abs(sin(iTime)) * ((pigs)* 10.);
    float scalar1 = sin(iTime * (mouse_x * cycle));
    float scalar2 = sin(iTime * (mouse_y * cycle));

    float red = mouse_x * (abs(sin((iTime * st.x) * scalar1)));
    float green = mouse_y * (abs(sin((iTime * st.y) * scalar2)));
    float blue = abs(scalar1)/abs(scalar2);

    fragColor = vec4(red, green, blue,1.0);
}
