float thresh = .4;
vec3 removeg(vec3 t, vec3 tex2) {

    if(t.x <= thresh && t.z <= thresh && t.y >= .5){
        return tex2;
    }
 	return vec3(t.x,(t.x + t.y + t.z)/3.,t.z);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	vec3 tex1 = texture(iChannel0, uv).xyz;
 	vec3 tex2 = texture(iChannel1, uv).xyz;
    // Output to screen
   	fragColor = vec4(removeg(tex1, tex2),1.);
}
